import React from "react";
import { useSelector } from "react-redux";

const MainPage = () => {
  const depts = useSelector((store) => store.depts);
  return (
    <>
      <div className="sub_container">
        <h2>컨텐츠영역</h2>
        <hr />
        {depts &&
          depts.map((dept, i) => (
            <h3 key={i}>
              {dept.DEPTNO},{dept.DNAME},{dept.LOC}
            </h3>
          ))}
      </div>
    </>
  );
};

export default MainPage;
